import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Komponent LoansArchive
const LoansArchive = () => {
  const [role, setRole] = useState('');
  const [archivedLoans, setArchivedLoans] = useState([]);

  useEffect(() => {
    // Tutaj powinno być zapytanie do API, aby pobrać role użytkownika i archiwalne wypożyczenia
    axios.get('/api/user/role').then(response => {
      setRole(response.data.role);
    });

    axios.get('/api/loans/archived').then(response => {
      setArchivedLoans(response.data.archivedLoans);
    });
  }, []);

  return (
    <div className="loans">
      <section>
        <div className="archive-reverse-container">
          <a href="/loans" className="archive-reverse-link">Wstecz</a>
        </div>

        <h2>Wypożyczenia archiwalne</h2>
        {role && (
          <table className="loans-table" id="loans-archive-table">
            <thead>
              <tr>
                {role === 'admin' && (
                  <>
                    <th>ID Użytkownika</th>
                    <th>Imię i Nazwisko</th>
                  </>
                )}
                <th>ID Egzemplarza</th>
                <th>Tytuł Książki</th>
                <th>Data Wypożyczenia</th>
                <th>Data Zwrotu</th>
              </tr>
            </thead>
            <tbody>
              {archivedLoans.length > 0 ? (
                archivedLoans.map((loan) => (
                  <tr key={loan.id}>
                    {role === 'admin' && (
                      <>
                        <td>{loan.userId}</td>
                        <td>{loan.userName}</td>
                      </>
                    )}
                    <td>{loan.copyId}</td>
                    <td>{loan.title}</td>
                    <td>{loan.borrowedDate}</td>
                    <td>{loan.actualReturnDate}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={role === 'admin' ? 6 : 4} className="no-results-message" id="loans-table-message-archive">
                    Tabela archiwalnych wypożyczeń jest pusta
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
};

export default LoansArchive;
