import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Folders.css'; // Załóżmy, że style CSS zostały przekształcone do odpowiedniego formatu dla React

const Folders = () => {
  const [plants, setPlants] = useState([]);

  useEffect(() => {
    // Załaduj dane roślin z API lub innego źródła
    axios.get('/api/plants')
      .then(response => setPlants(response.data))
      .catch(error => console.error('Error loading plants', error));
  }, []);

  return (
    <div className="base-container">
      <div className="plant-card">
        <img src="public/img/uploads/nice_plant.jpg" alt="Select a plant" />
        <div className="plantcard-name">select a plant</div>
        <div className="plantcard-water">Water</div>
        <div className="plantcard-fertilizer">Fertilize</div>
        <div className="plantcard-buttons">
          <button className="water-button" data-plant-id="0">
            <img src="public/img/water_button.svg" alt="Water the plant" />
          </button>
          <button className="fertilizer-button" data-plant-id="0">
            <img src="public/img/fertilizer_button.svg" alt="Fertilize the plant" />
          </button>
        </div>
      </div>
      <main>
        <section className="folders">
          {plants.map(plant => (
            <div key={plant.id} className="folder-container" data-plant-id={plant.id} data-water-last={plant.waterLast} data-fertilizer-last={plant.fertilizerLast}>
              <img src={`public/uploads/${plant.image}`} alt={plant.name} />
              <div className="name-container">
                <h2>{plant.name}</h2>
                <div className="notification"></div>
              </div>
            </div>
          ))}
          <div className="folder-container">
            <a href="http://localhost/web/addPlant">
              <img src="public/img/addPlantButton.svg" alt="Add new plant" />
            </a>
            <div className="name-container">
              <h2>add new</h2>
              <div className="notification"></div>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Folders;
