import React, { useState, useEffect } from 'react';
import { getUsers, updateUser, deleteUser } from '../services/userService'; // Zakładam istnienie takiego serwisu
import EditUserModal from './EditUserModal'; // Komponent modalny do edycji
import DeleteUserModal from './DeleteUserModal'; // Komponent modalny do usuwania

const UsersManagement = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      const fetchedUsers = await getUsers();
      setUsers(fetchedUsers);
    };

    fetchUsers();
  }, []);

  const handleEdit = (user) => {
    setSelectedUser(user);
  };

  const handleDelete = (userId) => {
    deleteUser(userId).then(() => {
      setUsers(users.filter((user) => user.id !== userId));
    });
  };

  return (
    <div className="users-management-container">
      <a href="/adminPanel" className="backwards-link">Wstecz</a>
      <table className="user-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>E-mail</th>
            <th>Imię i nazwisko</th>
            <th>Rola</th>
            <th>Operacje</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.email}</td>
                <td>{user.name} {user.lastname}</td>
                <td>{user.role === 'user' ? 'czytelnik' : user.role}</td>
                <td>
                  <button
                    className="user-management-buttons"
                    onClick={() => handleEdit(user)}
                  >
                    Edytuj
                  </button>
                  <button
                    className="user-management-buttons"
                    onClick={() => handleDelete(user.id)}
                  >
                    Usuń
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">Brak użytkowników do wyświetlenia</td>
            </tr>
          )}
        </tbody>
      </table>

      {selectedUser && (
        <EditUserModal
          user={selectedUser}
          closeModal={() => setSelectedUser(null)}
          updateUser={updateUser}
        />
      )}

      <DeleteUserModal
        confirmDelete={() => handleDelete(selectedUser?.id)}
        closeModal={() => setSelectedUser(null)}
      />
    </div>
  );
};

export default UsersManagement;
