import React, { useState, useEffect } from 'react';
import Header from './Header';
import './public/css/style.css';
import './public/css/table-styles.css';
import './public/css/modal-styles.css';

const Loans = () => {
    const [loans, setLoans] = useState([]);
    const [role, setRole] = useState('');
    const [showReturnModal, setShowReturnModal] = useState(false);
    const [showCancelModal, setShowCancelModal] = useState(false);

    // Załaduj dane po wejściu na stronę
    useEffect(() => {
        // Pobierz role z sesji i załaduj wypożyczenia
        // Implementacja zależna od backendu
    }, []);

    const handleReturn = (loanId) => {
        // Logika zwracania książki
        setShowReturnModal(true);
    };

    const handleCancel = (loanId) => {
        // Logika anulowania wypożyczenia
        setShowCancelModal(true);
    };

    return (
        <>
            <Header />
            <div className="archive-button-container">
                <a href="/loansArchive" className="loans-archive-link">Archiwum</a>
            </div>

            <section>
                {role === 'admin' && (
                    // Tabela dla administratora
                    <TableAdmin loans={loans} onReturn={handleReturn} onCancel={handleCancel} />
                )}

                {role === 'user' && (
                    // Tabela dla użytkownika
                    <TableUser loans={loans} />
                )}
            </section>

            {showReturnModal && <ReturnModalAdmin onClose={() => setShowReturnModal(false)} />}
            {showCancelModal && <CancelLoanModalAdmin onClose={() => setShowCancelModal(false)} />}
        </>
    );
};

// Komponenty dla modali i tabeli

export default Loans;
