import React, { useState } from 'react';
import axios from 'axios';
//import './Register.css'; // Assuming the CSS is adapted for React

const Register_l = () => {
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmedPassword, setConfirmedPassword] = useState('');
  const [messages, setMessages] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Data to be sent to the server
    const userData = { name, surname, email, password, confirmedPassword };

    // POST request to your server
    axios.post('/api/register', userData)
      .then(response => {
        // Handle the response
        console.log(response);
      })
      .catch(error => {
        // Handle errors
        setMessages(['Error during registration']);
      });
  };

  return (
    <div className="folder-form">
      <h1>Welcome to LeafLove</h1>
      <form onSubmit={handleSubmit}>
        <div className="messages">
          {messages.map((message, index) => <div key={index}>{message}</div>)}
        </div>
        <input name="name" type="name" placeholder="name" value={name} onChange={e => setName(e.target.value)} />
        <input name="surname" type="name" placeholder="surname" value={surname} onChange={e => setSurname(e.target.value)} />
        <input name="email" type="email" placeholder="email@email.com" value={email} onChange={e => setEmail(e.target.value)} />
        <input name="password" type="password" placeholder="password" value={password} onChange={e => setPassword(e.target.value)} />
        <input name="confirmedPassword" type="password" placeholder="confirm password" value={confirmedPassword} onChange={e => setConfirmedPassword(e.target.value)} />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default Register_l;
